import express from "express";
import { registerUser, loginUser, logoutUser, refreshingAccessToken, changeCurrentPassword, } from "../controllers/auth.controllers.js";
import { authenticate } from "../middlewares/auth.middlewares.js";
const router = express.Router();
router.post("/register", registerUser);
router.post("/login", loginUser);
router.post("/logout", authenticate, logoutUser);
router.post("/refresh-token", refreshingAccessToken);
router.post("/change-password", authenticate, changeCurrentPassword);
export default router;
